import React from 'react';
import {ForceGraph, ForceGraphNode, ForceGraphLink} from 'react-vis-force';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';


var data = JSON.parse(require('./data.json'));
var config = JSON.parse(require('./config.json'));

console.log(data)
console.log(config)

const GraphConfig = config

const NODE_KEY = "id"       // Allows D3 to correctly update DOM

class Graph extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      graph: data,
      selected: {},
      config: config
    }
  }

  render() {
    const config = this.state.config;
    const nodes = this.state.graph.nodes.slice(1,100);
    const edges = this.state.graph.edges.slice(1,100);
    return (
      <Container>
      {/* <ForceGraph simulationOptions={{ height: 300, width: 300 }}> */}
      {nodes.map(node => {
        // <ForceGraphNode node={{ id: node.id }} fill="red" />

        return (<p>{node.id}</p>)
      })
    }
        {edges.map(edge => {
        // <ForceGraphLink link={{ source: edge.source, target: edge.target }} />
        return (<p>{edge.source}</p>) 
      })}

      {/* </ForceGraph> */}
      </Container>
    );
  }

}

export default Graph;
