import React from 'react';
import { Button, ButtonGroup, Dropdown } from 'react-bootstrap';


class DataBuilder extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            config : this.buildconfig(),
            data : this.builddata()
        };

        this.buttonClick = this.buttonClick.bind(this);
        this.handleClusterQuery = this.handleClusterQuery.bind(this);
        this.getRelation
    }

    buildconfig() {

        var config = JSON.parse(require('./config.json'))

        var NodeTypes = {}

        config.NodeTypes.map( type => {
            type.shape =  (
                    <symbol viewBox="0 0 100 100" id={type.typeText} key="0">
                        <circle cx="50" cy="50" r="45" style={{ color : '#ffffff' , 
                                                                fill : type.color}} ></circle>
                    </symbol>
                    )
            NodeTypes[type.typeText] = type

        })

        var EdgeTypes = {}

        config.EdgeTypes.map( type => {
            type.shape = (
                    <symbol viewBox="0 0 200 200" id={type.typeText} key="0" label_from = {type.typeText}>
                        <circle cx="100" cy="100" r="45" fill={type.color}></circle>
                    </symbol>
            )
            EdgeTypes[type.typeText] = type
        })

        var PotterConfig =  {
            NodeTypes: NodeTypes,
            NodeSubtypes: {},
            EdgeTypes: EdgeTypes
          }

        return PotterConfig;
    }

    builddata() {
        var data = JSON.parse(require('./data.json'))   
        return data
    }

    async getRelation() {

        var query = { texts: this.state.query }
        const response = await fetch('http://localhost:5000', {
        method: 'POST', // or 'PUT'
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(query),
        })

        // .then ( data => 
        //     data.json()
        //     );

        // this.setState({
        //     predictions:predictions
        //     })
        // console.log(this.state.toxic)
      }

    buttonClick = (event) => {
        var packet = {
            config : this.buildconfig(),
            data : this.builddata()
        }

        this.setState(packet);

        this.props.parentCallback(packet)

        event.preventDefault();
    }

    handleClusterQuery  = (e) => {
        this.setState({query : e});
        this.getRelation();
    }

    render(){
        return (
            <ButtonGroup>
                <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        Dropdown Button
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item onSelect = {this.handleClusterQuery} >cluster0</Dropdown.Item>
                        <Dropdown.Item onSelect = {this.handleClusterQuery} >cluster1</Dropdown.Item>
                        <Dropdown.Item onSelect = {this.handleClusterQuery} >cluster2</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
                <Button onClick = {this.buttonClick}> Get Data </Button>
            </ButtonGroup>

        )}
}


export default DataBuilder;
